     let data = draw.getAll();
     this.map.removeLayer('maine').removeSource('maine');
}
updateArea(e) {
      let data = draw.getAll();
      this.map.removeLayer('maine').removeSource('maine');
      const polygonData = data.features[0].geometry.coordinates;
      this.drawPolygon(polygonData);
      this.polygonDataCalc(data);
}



